export const NotificationType = {
  INFO: 'info',
  SUCCESS: 'success',
  WARNING: 'warning',
  ERROR: 'error',
  CANDIDATE: 'candidate',
  INTERVIEW: 'interview',
  APPROVAL: 'approval',
};

/**
 * Notification sounds - base64 encoded short beep sounds
 * These are tiny audio files for instant playback without external dependencies
 */
const NOTIFICATION_SOUNDS = {
  default:
    'data:audio/mp3;base64,',
  success:
    'data:audio/mp3;base64,',
  warning:
    'data:audio/mp3;base64,',
  error:
    'data:audio/mp3;base64,',
};

function getSoundForType(type) {
  switch (type) {
    case NotificationType.SUCCESS:
      return NOTIFICATION_SOUNDS.success;
    case NotificationType.WARNING:
    case NotificationType.APPROVAL:
      return NOTIFICATION_SOUNDS.warning;
    case NotificationType.ERROR:
      return NOTIFICATION_SOUNDS.error;
    default:
      return NOTIFICATION_SOUNDS.default;
  }
}

/**
 * Best-effort sound playback. Browsers may block autoplay without user interaction.
 */
export function playNotificationSound(type = NotificationType.INFO, volume = 0.5) {
  try {
    const AudioContextCtor = window.AudioContext || window.webkitAudioContext;
    if (AudioContextCtor) {
      const audioContext = new AudioContextCtor();
      const oscillator = audioContext.createOscillator();
      const gainNode = audioContext.createGain();

      oscillator.connect(gainNode);
      gainNode.connect(audioContext.destination);

      const frequencies = {
        [NotificationType.INFO]: 800,
        [NotificationType.SUCCESS]: 1000,
        [NotificationType.WARNING]: 600,
        [NotificationType.ERROR]: 400,
        [NotificationType.CANDIDATE]: 900,
        [NotificationType.INTERVIEW]: 850,
        [NotificationType.APPROVAL]: 700,
      };

      oscillator.type = 'sine';
      oscillator.frequency.setValueAtTime(frequencies[type] || 800, audioContext.currentTime);

      gainNode.gain.setValueAtTime(Math.max(0, Math.min(1, volume)), audioContext.currentTime);
      gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + 0.3);

      oscillator.start(audioContext.currentTime);
      oscillator.stop(audioContext.currentTime + 0.3);
      return;
    }
  } catch {
    // fall through to HTML5 Audio
  }

  try {
    const audio = new Audio(getSoundForType(type));
    audio.volume = Math.max(0, Math.min(1, volume));
    audio.play().catch(() => {});
  } catch {
    // ignore
  }
}

